def publish_display_data(data):
    ''' Provide the same function for Zeppelin that is used for IPython to 
        publish Bokeh html and javascript
    '''
    html = data.get("text/html")
    js = data.get("application/javascript")
    comms_target = data.get("comms_target") 
    # TODO: Error handling if comms_target is missing
    
    print("%html\n")
    if comms_target is not None:
        print("<div id=\"comms_" + comms_target + "\">")
    if html:
        print(html + "\n")
    if js:
        print("<script>\n" + js + "\n</script>\n")
    if comms_target is not None:
        print('</div>')

def push_data(target, json):
    print(json)
